import React, { useState, useEffect } from 'react';
import { BrowserRouter, Routes, Route, Navigate, useNavigate, Link } from 'react-router-dom';

const API = 'http://localhost:5000/api';

// Auth Context
const AuthContext = React.createContext();
const useAuth = () => React.useContext(AuthContext);

function AuthProvider({ children }) {
  const [user, setUser] = useState(() => {
    const saved = localStorage.getItem('user');
    return saved ? JSON.parse(saved) : null;
  });

  const login = (data) => {
    localStorage.setItem('user', JSON.stringify(data));
    setUser(data);
  };

  const logout = () => {
    localStorage.removeItem('user');
    setUser(null);
  };

  return (
    <AuthContext.Provider value={{ user, login, logout }}>
      {children}
    </AuthContext.Provider>
  );
}

// Navbar
function Navbar() {
  const { user, logout } = useAuth();
  const navigate = useNavigate();

  return (
    <nav className="navbar">
      <h1 onClick={() => navigate('/')} style={{ cursor: 'pointer' }}>üé¨ BookMyShow</h1>
      {user && (
        <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
          <span>{user.email}</span>
          <button onClick={() => { logout(); navigate('/login'); }}>Logout</button>
        </div>
      )}
    </nav>
  );
}

// Login
function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API}/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error);
      login(data);
      navigate('/');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>Login</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
        <button type="submit">Login</button>
      </form>
      <p>Don't have an account? <Link to="/signup">Sign up</Link></p>
    </div>
  );
}

// Signup
function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const { login } = useAuth();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await fetch(`${API}/signup`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error);
      login(data);
      navigate('/');
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="auth-container">
      <h2>Sign Up</h2>
      {error && <p className="error">{error}</p>}
      <form onSubmit={handleSubmit}>
        <input type="email" placeholder="Email" value={email} onChange={e => setEmail(e.target.value)} required />
        <input type="password" placeholder="Password" value={password} onChange={e => setPassword(e.target.value)} required />
        <button type="submit">Sign Up</button>
      </form>
      <p>Already have an account? <Link to="/login">Login</Link></p>
    </div>
  );
}

// Movies List
function Movies() {
  const [movies, setMovies] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`${API}/movies`).then(r => r.json()).then(setMovies);
  }, []);

  return (
    <div className="movies-container">
      <h2>Now Showing</h2>
      <div className="movies-grid">
        {movies.map(movie => (
          <div key={movie.id} className="movie-card" onClick={() => navigate(`/movie/${movie.id}`)}>
            <img src={movie.poster} alt={movie.name} />
            <h3>{movie.name}</h3>
          </div>
        ))}
      </div>
    </div>
  );
}

// Show Times
function ShowTimes() {
  const [movie, setMovie] = useState(null);
  const navigate = useNavigate();
  const movieId = window.location.pathname.split('/')[2];

  useEffect(() => {
    fetch(`${API}/movies/${movieId}`).then(r => r.json()).then(setMovie);
  }, [movieId]);

  if (!movie) return <div>Loading...</div>;

  return (
    <div className="shows-container">
      <h2>{movie.name}</h2>
      <h3>Select Show Time</h3>
      <div className="shows-grid">
        {movie.shows.map(show => (
          <button key={show.id} className="show-btn" onClick={() => navigate(`/seats/${movie.id}/${show.id}`)}>
            {show.time}
          </button>
        ))}
      </div>
      <button className="back-btn" onClick={() => navigate('/')}>‚Üê Back to Movies</button>
    </div>
  );
}

// Seat Selection
function SeatSelection() {
  const [movie, setMovie] = useState(null);
  const [selectedSeats, setSelectedSeats] = useState([]);
  const [error, setError] = useState('');
  const { user } = useAuth();
  const navigate = useNavigate();
  const parts = window.location.pathname.split('/');
  const movieId = parts[2];
  const showId = parts[3];

  useEffect(() => {
    fetch(`${API}/movies/${movieId}`).then(r => r.json()).then(setMovie);
  }, [movieId]);

  if (!movie) return <div>Loading...</div>;

  const show = movie.shows.find(s => s.id === showId);

  const toggleSeat = (index) => {
    if (show.seats[index]) return;
    setSelectedSeats(prev => 
      prev.includes(index) ? prev.filter(s => s !== index) : [...prev, index]
    );
  };

  const confirmBooking = async () => {
    try {
      const res = await fetch(`${API}/bookings`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${user.token}`
        },
        body: JSON.stringify({ movieId, showId, seats: selectedSeats })
      });
      const data = await res.json();
      if (!res.ok) throw new Error(data.error);
      navigate('/confirmation', { state: data });
    } catch (err) {
      setError(err.message);
    }
  };

  return (
    <div className="seats-container">
      <h2>{movie.name}</h2>
      <p style={{ marginBottom: '1rem', opacity: 0.7 }}>{show.time}</p>
      <div className="screen">SCREEN</div>
      
      <div className="seats-grid">
        {show.seats.map((booked, i) => (
          <div
            key={i}
            className={`seat ${booked ? 'booked' : ''} ${selectedSeats.includes(i) ? 'selected' : ''}`}
            onClick={() => toggleSeat(i)}
          >
            {i + 1}
          </div>
        ))}
      </div>

      <div className="seat-legend">
        <div className="legend-item">
          <div className="legend-box" style={{ border: '2px solid #e94560' }}></div>
          <span>Available</span>
        </div>
        <div className="legend-item">
          <div className="legend-box" style={{ background: '#e94560' }}></div>
          <span>Selected</span>
        </div>
        <div className="legend-item">
          <div className="legend-box" style={{ background: '#444' }}></div>
          <span>Booked</span>
        </div>
      </div>

      {error && <p className="error">{error}</p>}
      
      <button className="confirm-btn" disabled={selectedSeats.length === 0} onClick={confirmBooking}>
        Book {selectedSeats.length} Seat{selectedSeats.length !== 1 ? 's' : ''}
      </button>
      
      <button className="back-btn" onClick={() => navigate(`/movie/${movieId}`)}>‚Üê Back to Shows</button>
    </div>
  );
}

// Confirmation
function Confirmation() {
  const navigate = useNavigate();
  const booking = window.history.state?.usr;

  if (!booking) {
    return <Navigate to="/" />;
  }

  return (
    <div className="confirmation-container">
      <div className="checkmark">‚úì</div>
      <h2>Booking Confirmed!</h2>
      <div className="booking-details">
        <p><span>Movie:</span> {booking.movieName}</p>
        <p><span>Show Time:</span> {booking.showTime}</p>
        <p><span>Seats:</span> {booking.seats.join(', ')}</p>
        <p><span>Booking ID:</span> {booking.id}</p>
      </div>
      <button className="confirm-btn" onClick={() => navigate('/')}>Book More Tickets</button>
    </div>
  );
}

// Protected Route
function ProtectedRoute({ children }) {
  const { user } = useAuth();
  return user ? children : <Navigate to="/login" />;
}

// App
function App() {
  return (
    <BrowserRouter>
      <AuthProvider>
        <div className="app">
          <Navbar />
          <Routes>
            <Route path="/login" element={<Login />} />
            <Route path="/signup" element={<Signup />} />
            <Route path="/" element={<ProtectedRoute><Movies /></ProtectedRoute>} />
            <Route path="/movie/:id" element={<ProtectedRoute><ShowTimes /></ProtectedRoute>} />
            <Route path="/seats/:movieId/:showId" element={<ProtectedRoute><SeatSelection /></ProtectedRoute>} />
            <Route path="/confirmation" element={<ProtectedRoute><Confirmation /></ProtectedRoute>} />
          </Routes>
        </div>
      </AuthProvider>
    </BrowserRouter>
  );
}

export default App;
